package com.example.course.services;import com.example.course.entities.User;import com.example.course.repositories.UserRepositoy;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class UserService {    @Autowired    private UserRepositoy userRepositoy;    public List<User> findAll() {        return userRepositoy.findAll();    }    public User findById(Long id) {        Optional<User> obj = userRepositoy.findById(id);        return obj.get();    }}